jQuery(document).ready(function($){const locale='da';const defaultAmount=1;const decimalCharacter=',';const digitGroupSeparator='.';try{var input=new AutoNumeric('#ws-co2-calculator-amount',{showWarnings:!1,allowDecimalPadding:!1,decimalCharacter:decimalCharacter,digitGroupSeparator:digitGroupSeparator,})}catch(e){console.error('AutoNumeric Exception: ',e)}
var type='steel';const typeLabels={'steel':'steel scrap','stainless_steel':'stainless steel scrap'}
const co2Factors={'steel':1.67,'stainless_steel':4.30};const co2Savings={'steel':213,'stainless_steel':213}
const throughputPerYear={'steel':425000,'stainless_steel':6000}
var factor,saving,amount;factor=co2Factors[type];saving=co2Savings[type];amount=defaultAmount;$('#ws-co2-calculator-calculate').click(function(e){e.preventDefault();type=$('#ws-co2-calculator-type').val();$('#ws-co2-calculator-type-label').text(typeLabels[type]);factor=co2Factors[type];cost=co2Savings[type];amount=defaultAmount;if(input.getNumericString()!==''){amount=input.getNumber()}
calculateSavings();refreshStatistics()});function calculateSavings(){const reduction=amount*factor;$('#ws-co2-calculator-reduction').text(reduction.toLocaleString(locale,{minimumFractionDigits:2,maximumFractionDigits:2}));const savings=reduction*saving;$('#ws-co2-calculator-savings').text(savings.toLocaleString(locale,{style:'currency',currency:'EUR'}))}
function refreshStatistics(){const then=(new Date(2021,0,1,0,0,0,0)).getTime();const now=Date.now();const throughputPerSecond=throughputPerYear[type]/365/24/60/60;const throughputToDate=throughputPerSecond*((now-then)/1000);$('#ws-co2-calculator-stats-total').text(throughputToDate.toLocaleString(locale,{maximumFractionDigits:0}));const reduction=throughputToDate*factor;$('#ws-co2-calculator-stats-reduction').text(reduction.toLocaleString(locale,{maximumFractionDigits:0}))}
setInterval(refreshStatistics,1000);calculateSavings();refreshStatistics()})